
public class RestourantException extends Exception {

	private static final long serialVersionUID = 1L;

	public RestourantException() {
		super();
	}

	public RestourantException(String message, Throwable cause, boolean enableSuppression, boolean writableStackTrace) {
		super(message, cause, enableSuppression, writableStackTrace);
	}

	public RestourantException(String message, Throwable cause) {
		super(message, cause);
	}

	public RestourantException(String message) {
		super(message);
	}

	public RestourantException(Throwable cause) {
		super(cause);
		// TODO Auto-generated constructor stub
	}

}
